DROP TABLE IF EXISTS `bg_blog`;
CREATE TABLE `bg_blog` (
  `blog_id` bigint NOT NULL AUTO_INCREMENT COMMENT ' id',
  `category_id` bigint DEFAULT NULL COMMENT ' 分类id ',
  `title` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT ' 文章标题 ',
  `summary` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT ' 摘要 ',
  `header_img_type` int NOT NULL,
  `header_img` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT ' 封面图片地址 ',
  `html_content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci,
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT ' 正文内容 ',
  `status` tinyint DEFAULT NULL COMMENT ' 文章状态,\n    1表示已经发表,\n    2表示草稿箱 ',
  `comment` tinyint DEFAULT NULL COMMENT ' 是否允许评论,\n    1表示允许,\n    0表示不允许 ',
  `support` tinyint DEFAULT '0' COMMENT ' 推荐 ',
  `weight` bigint DEFAULT '1' COMMENT ' 权重 ',
  `like` bigint DEFAULT '0' COMMENT ' 点赞数 ',
  `click` bigint DEFAULT '0' COMMENT ' 点击数 ',
  `create_by` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `create_time` datetime DEFAULT NULL,
  `update_by` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  PRIMARY KEY (`blog_id`) USING BTREE,
  KEY `blog_id_support_status_index` (`blog_id`,`support`,`status`) USING BTREE
