mapper.select:
select
id, description, click, img_url, display, target, create_by, create_time, update_by, update_time, delete_by,
delete_time
from sys_carousel


        and id = #{id}


        and description = #{description}


        and click = #{click}


        and img_url = #{imgUrl}


        and display = #{display}


        and target = #{target}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and delete_by = #{deleteBy}


        and delete_time = #{deleteTime}
