mapper.select:
select id, job_name, bean_name, method_name, method_params, cron_expression, status, remark, create_time, create_by from tool_quartz_job where delete_time is null
and name like concat('%',#{name},'%')
and suffix = #{suffix}
and type = #{type}
and date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

select id, real_name, name, suffix, path, type, size, create_by, create_time
from tool_local_storage
where delete_time is null and id=#{id}
